div.script:
document.addEventListener("DOMContentLoaded", function () {

    document.querySelectorAll("[data-qv]").forEach(function (btn) {
        btn.addEventListener("click", function () {

            document.getElementById("qvTitle").textContent = this.dataset.title;
            document.getElementById("qvImage").src = this.dataset.image;
            document.getElementById("qvDescription").textContent = this.dataset.desc;

            const modal = document.getElementById("productQuickViewModal");
            modal.style.display = "block";
            modal.classList.add("show");
            document.body.classList.add("modal-open");

            if (!document.querySelector(".modal-backdrop")) {
                const backdrop = document.createElement("div");
                backdrop.className = "modal-backdrop fade show";
                document.body.appendChild(backdrop);
            }
        });
    });

    document.addEventListener("click", function (e) {
        if (e.target.dataset.bsDismiss === "modal") {
            const modal = document.getElementById("productQuickViewModal");
            modal.classList.remove("show");
            modal.style.display = "none";
            document.body.classList.remove("modal-open");
            document.querySelector(".modal-backdrop")?.remove();
        }
    });

});

document.addEventListener("DOMContentLoaded", function () {
    document.querySelectorAll(".wishlist-btn").forEach(function (btn) {
        btn.addEventListener("click", function () {
            this.classList.toggle("active");
        });
    });
});

script:
document.addEventListener('DOMContentLoaded', function() {

    const counters = document.querySelectorAll('.stat-number');

    counters.forEach(counter => {
        const rawValue = counter.getAttribute('data-count');

        // extract number (supports decimal)
        const numberMatch = rawValue.match(/[\d.]+/);
        const target = numberMatch ? parseFloat(numberMatch[0]) : 0;

        // extract suffix like + or /10
        const suffix = rawValue.replace(numberMatch[0], '');

        const duration = 2000;
        const increment = target / (duration / 16);
        let current = 0;

        const updateCounter = () => {
            current += increment;
            if (current < target) {
                counter.textContent =
                    (target % 1 === 0 ? Math.floor(current) : current.toFixed(1)) + suffix;
                setTimeout(updateCounter, 16);
            } else {
                counter.textContent = target + suffix;
            }
        };

        const observer = new IntersectionObserver(entries => {
            entries.forEach(entry => {
                if (entry.isIntersecting) {
                    updateCounter();
                    observer.unobserve(entry.target);
                }
            });
        }, { threshold: 0.5 });

        observer.observe(counter);
    });

});
