script:
// Simple particle effect for background
document.addEventListener('DOMContentLoaded', function() {
    // Create particles
    const particlesContainer = document.querySelector('.particles');
    if (particlesContainer) {
        for (let i = 0; i < 30; i++) {
            const particle = document.createElement('div');
            particle.style.position = 'absolute';
            particle.style.width = Math.random() * 5 + 2 + 'px';
            particle.style.height = particle.style.width;
            particle.style.background = `rgba(255, ${Math.random() * 100 + 155}, ${Math.random() * 100 + 100}, ${Math.random() * 0.5 + 0.3})`;
            particle.style.borderRadius = '50%';
            particle.style.top = Math.random() * 100 + '%';
            particle.style.left = Math.random() * 100 + '%';
            particle.style.boxShadow = '0 0 10px rgba(255,255,255,0.5)';

            // Animation
            particle.animate([
                { transform: 'translateY(0px)' },
                { transform: `translateY(${Math.random() * 100 - 50}px) translateX(${Math.random() * 100 - 50}px)` }
            ], {
                duration: Math.random() * 5000 + 5000,
                iterations: Infinity,
                direction: 'alternate'
            });

            particlesContainer.appendChild(particle);
        }
    }

    // Counter animation
    const counters = document.querySelectorAll('.counter-number');
    const speed = 200; // Lower is faster

    counters.forEach(counter => {
        const updateCount = () => {
            const target = +counter.getAttribute('data-count');
            const count = +counter.innerText;

            const inc = target / speed;

            if (count < target) {
                counter.innerText = Math.ceil(count + inc);
                setTimeout(updateCount, 20);
            } else {
                counter.innerText = target + (target >= 98 ? '%' : target >= 200 ? '+' : 'K+');
            }
        };

        // Start counter when in viewport
        const observer = new IntersectionObserver((entries) => {
            if (entries[0].isIntersecting) {
                updateCount();
                observer.unobserve(counter);
            }
        });

        observer.observe(counter);
    });

    // Card hover effects enhancement
    const cards = document.querySelectorAll('.info-card');
    cards.forEach(card => {
        card.addEventListener('mouseenter', function() {
            this.style.zIndex = '100';
        });

        card.addEventListener('mouseleave', function() {
            this.style.zIndex = '1';
        });
    });
});
